def add_new_questions(questions_to_add):
    """整理された問題をデータベースに追加する"""
    try:
        conn = sqlite3.connect('kashikin.db')
        cursor = conn.cursor()

        # 問題が既に存在しないか確認して挿入
        for q in questions_to_add:
            cursor.execute(
                "SELECT id FROM questions WHERE year = ? AND genre = ? AND question = ?",
                (q['year'], q['genre'], q['question'])
            )
            if cursor.fetchone() is None:
                cursor.execute('''
                    INSERT INTO questions (year, genre, question, answer, commentary)
                    VALUES (?, ?, ?, ?, ?)
                ''', (q['year'], q['genre'], q['question'], q['answer'], q['commentary']))
        
        conn.commit()
        print(f"データベースに {len(questions_to_add)} 問の問題を追加しました。")

    except sqlite3.Error as e:
        print(f"データベースエラー: {e}")
    finally:
        if conn:
            conn.close()

if __name__ == '__main__':
    add_new_questions(new_questions_to_add)